  // ==========================================================
  //  2. FUNCIÃ“N PARA RENDERIZAR EL CONTENIDO EN EL HTML
  // ==========================================================
  function renderContent() {
    const appContainer = document.getElementById("app-container");
    if (!appContainer) return;

    let allHtml = "";

    // Recorrer cada fiesta en el objeto de datos (SHABAT, PESAJ, etc.)
    for (const fiestaKey in studiesData) {
      const fiesta = studiesData[fiestaKey];
      const estudios = fiesta.es;

      // Iniciar la secciÃ³n de la fiesta
      let seccionHtml = `
                <section class="fiesta-section">
                    <h1>${fiestaKey}</h1>
                    <p class="subtitulo">${estudios.length} estudios</p>
            `;

      // AÃ±adir cada estudio como un acordeÃ³n
      estudios.forEach((estudio, index) => {
        // Reemplazamos los saltos de lÃ­nea (\n) por etiquetas <br> para que se vean en HTML
        const contenidoHtml = estudio.content.replace(/\n/g, "<br>");

        seccionHtml += `
                    <div class="estudio-acordeon">
        <button class="acordeon-header">
            <div class="acordeon-header-text">
                <span class="acordeon-title">${estudio.title}</span>
                <p class="acordeon-summary">${estudio.summary}</p> </div>
            <i class="fa-solid fa-chevron-right acordeon-arrow"></i>
        </button>
                        <div class="acordeon-content">
                            <div class="estudio-texto">
                                <p>${contenidoHtml}</p>
                            </div>
                            <div class="acciones">
                                <div class="social-share">
                                    <span>Compartir:</span>
                                    <a href="#" class="share-btn facebook" target="_blank" title="Compartir en Facebook"><i class="fa-brands fa-facebook"></i></a>
                                    <a href="#" class="share-btn twitter" target="_blank" title="Compartir en Twitter/X"><i class="fa-brands fa-twitter"></i></a>
                                    <a href="#" class="share-btn whatsapp" target="_blank" title="Compartir en WhatsApp"><i class="fa-brands fa-whatsapp"></i></a>
                                </div>
                                <button class="btn-copiar">Copiar Contenido</button>
                            </div>
                        </div>
                    </div>
                `;
      });

      // Cerrar la secciÃ³n
      seccionHtml += `</section>`;
      allHtml += seccionHtml;
    }

    // Inyectar todo el HTML generado en el contenedor principal
    appContainer.innerHTML = allHtml;
  }

  // ==========================================================
  //  3. FUNCIÃ“N PARA AÃ‘ADIR LOS EVENTOS A LOS ELEMENTOS CREADOS
  // ==========================================================
  function attachEventListeners() {
    // --- LÃ“GICA DEL ACORDEÃ“N ---
    const acordeones = document.querySelectorAll(".acordeon-header");
    acordeones.forEach((acordeon) => {
      acordeon.addEventListener("click", () => {
        acordeon.classList.toggle("active");

        // CÃ“DIGO CORREGIDO Y MÃS ROBUSTO
        const parent = acordeon.parentElement;
        const content = parent.querySelector(".acordeon-content");

        if (content.style.maxHeight) {
          content.style.maxHeight = null;
        } else {
          content.style.maxHeight = content.scrollHeight + "px";
        }
      });
    });

    // --- LÃ“GICA PARA COPIAR CONTENIDO --- (Sin cambios)
    const botonesCopiar = document.querySelectorAll(".btn-copiar");
    botonesCopiar.forEach((boton) => {
      boton.addEventListener("click", (e) => {
        const textoEstudio = e.target
          .closest(".acordeon-content")
          .querySelector(".estudio-texto").innerText;
        navigator.clipboard.writeText(textoEstudio).then(() => {
          const textoOriginal = boton.innerText;
          boton.innerText = "Â¡Copiado!";
          boton.style.backgroundColor = "#2ecc71";
          setTimeout(() => {
            boton.innerText = textoOriginal;
            boton.style.backgroundColor = "#3498db";
          }, 2000);
        });
      });
    });

    // --- LÃ“GICA PARA COMPARTIR EN REDES SOCIALES --- (Sin cambios)
    const botonesCompartir = document.querySelectorAll(".share-btn");
    const pageUrl = window.location.href;
    botonesCompartir.forEach((boton) => {
      boton.addEventListener("click", (e) => {
        e.preventDefault();
        const tituloEstudio = boton
          .closest(".estudio-acordeon")
          .querySelector(".acordeon-title").innerText;
        const textoCompartir = encodeURIComponent(
          `Echa un vistazo a este estudio: "${tituloEstudio}"\n\n${pageUrl}`
        );
        let shareUrl = "";
        if (boton.classList.contains("facebook"))
          shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(
            pageUrl
          )}`;
        if (boton.classList.contains("twitter"))
          shareUrl = `https://twitter.com/intent/tweet?text=${textoCompartir}`;
        if (boton.classList.contains("whatsapp"))
          shareUrl = `https://api.whatsapp.com/send?text=${textoCompartir}`;
        if (shareUrl) window.open(shareUrl, "_blank", "width=600,height=400");
      });
    });
  }

  // ==========================================================
